
public class Ex202 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String str1; //string 에서 앞은 대문자
		str1="java class1";
		System.out.println(str1);
		
		String str2; //대문자
		str2="java class2";
		System.out.println(str2);
		
		String str3; //대문자
		str3=str1+str2;
		System.out.println(str3);
		
		//문자열의 합 즉 문자열을 출력하는 string 기억하기
	}

}
