
public class Ex305 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x=10, y=3;
		int result;
		result=x+y;
		System.out.println(result);
		result=x-y;
		System.out.println(result);
		result=x*y;
		System.out.println(result);
		result=x%y;
		System.out.println(result);
		result=x/y;
		System.out.println(result);
		
		double result2; //double8바이트 실수형
		result2=x/y;
		System.out.println(result2);
		result2=(double)x/y; //int타입은 double타입으로 형변환
		System.out.println(result2);

	}

}
